#include "StdInc.h"

template <typename T>
class CTimeCycleProperty : public CTimeCyclePropertyInterface
{
public:
    CTimeCycleProperty(T* tAddress, std::vector<T> m_Values, bool bDecimal = false)
    {
        tMemoryAddress   = tAddress;
        m_OriginalValues = m_Values;
        bIsDecimal       = bDecimal;
    }
    ~CTimeCycleProperty() {}

    bool ResetValue(int iWeatherID = -1, int iCycleNum = -1)
    {
        if (iWeatherID == -1 && iCycleNum == -1)
        {
            // restore whole memory block
            MemCpyFast((void*)tMemoryAddress, m_OriginalValues.data(), sizeof(T) * 184);
        }
        else
        {
            int iOffset = iWeatherID + iCycleNum * 23;
            MemPutFast<T>((void*)(tMemoryAddress + iOffset), m_OriginalValues[iOffset]);
        }
        return true;
    }
    bool SetValue(float fValue, int iWeatherID = -1, int iCycleNum = -1)
    {
        if (bIsDecimal) fValue *= 10;
        if (iWeatherID == -1 && iCycleNum == -1)
        {
            // set value to a whole memory block
            T tValues[184];
            std::fill_n(tValues, 184, (T)fValue);
            MemCpyFast((void*)tMemoryAddress, &tValues, sizeof(tValues));
        }
        else
        {
            MemPutFast<T>((void*)(tMemoryAddress + iWeatherID + iCycleNum * 23), (T)fValue);
        }
        return true;
    }
    float GetValue(int iWeatherID, int iCycleNum)
    {
        float fValue = *(tMemoryAddress + iWeatherID + iCycleNum * 23);
        if (bIsDecimal) fValue *= 0.1F;
        return fValue;
    }
    float GetOriginalValue(int iWeatherID, int iCycleNum)
    {
        float fValue = m_OriginalValues[iWeatherID + iCycleNum * 23];
        if (bIsDecimal) fValue *= 0.1F;
        return fValue;
    }
private:
    T* tMemoryAddress;
    std::vector<T> m_OriginalValues;
    bool bIsDecimal;
};

